                numberOfIterations: 1500,
                randomSeed: 1,
                numberOfDisplayedTopTopics: 3,
                displayedTopicsTopWords: 8,
                removeHighFrequentWords: 0.6,
                ignoreCapitalization: false,
                removeNumbers: true,
                stemming: false,
                removeStopwords: false,
                minimalWordLength: 2,
                timespan: 14,
                selectedTab: 'lda'
            },
            comments: [],
            result: null,
            loading: false,
            topicbarData: {},
