                                   }} value={this.state.ldaConfig.displayedTopicsTopWords}/>
                        <SelectField floatingLabelText={"Stammwortreduktion"} onChange={(e, index, newValue) => {
                            this.setConfig('stemming', newValue)
                        }} value={this.state.ldaConfig.stemming}>
