                            <MenuItem primaryText={"Nein"} value={false}/>
                        </SelectField>
                        <SelectField floatingLabelText={"Stoppwortreduktion"} onChange={(e, index, newValue) => {
                            this.setConfig('removeStopwords', newValue)
                        }} value={this.state.ldaConfig.removeStopwords}>
